ted, a.forumlink:link, a.forumlink:active {\n	color: #002A46;\n	font-weight: bold;\n	font-family: "Lucida Grande", Helvetica, Arial, sans-serif;\n	font-size: 1.3em;\n}\n\na.forumlink:hover {\n	color: #005893;\n	font-weight: bold;\n	font-family: "Lucida Grande", Helvetica, Arial, sans-serif;\n	font-size: 1.3em;\n}\n\na.topictitle, a.topictitle:link, a.topictitle:active {\n	color: #002A46;	\n	margin: 1px 0;\n	font-family: "Lucida Grande", Helvetica, Arial, sans-serif;\n	font-weight: bold;\n	font-size: 1.2em;\n}\n\na.topictitle:visited, a.topictitle:hover {\n	color: #005893;	\n	margin: 1px 0;\n	font-family: "Lucida Grande", Helvetica, Arial, sans-serif;\n	font-weight: bold;\n	font-size: 1.2em;\n}\n\nth a, th a:visited {\n	color: #FFFFFF !important;\n	text-decoration: none;\n}\n\nth a:hover {\n	text-decoration: underline;\n}\n\n\n/* Form Elements\n ------------ */\nform {\n	margin: 0;\n	padding: 0;\n	border: 0;\n}\n\ninput {\n	background-color: #FAFAFA;\n	color: #000000;\n	font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;\n	font-size: 1.1em;\n	font-weight: normal;\n	padding: 1px;\n	border: 1px solid #566877;\n}\n\ntextarea {\n	background-color: #FAFAFA;\n	color: #000000;\n	font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;\n	font-size: 1.3em; \n	line-height: 1.4em;\n	font-weight: normal;\n	border: 1px solid #566877;\n	padding: 2px;\n}\n\nselect {\n	color: #000000;\n	background-color: #FAFAFA;\n	font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;\n	font-size: 1.1em;\n	font-weight: normal;\n	border: 1px solid #3A4955;\n	padding: 1px;\n}\n\noption {\n	padding: 0 1em 0 0;\n}\n\noption.disabled-option {\n	color: graytext;\n}\n\n.rtl option {\n	padding: 0 0 0 1em;\n}\n\ninput.radio {\n	margin-top: 2px;\n	margin-left: 0px;\n	margin-right: 0px;\n	border: none;\n	background-color: transparent;\n}\n\n.post {\n	background-color: #FAFAFA;\n	background-image: url(''styles/AeroBlack/theme/images/bginput.gif'');\n	background-repeat: repeat-x;\n	border-style: solid;\n	border-width: 1px;\n}\n\n.btnbbcode {\n	color: #000000;\n	font-weight: normal;\n	font-size: 1.1em;\n	font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -0px;\n	border: 1px solid #3A4955;\n}\n\n.btnbbcode:hover {\n	color: #000000;\n	font-weight: normal;\n	font-size: 1.1em;\n	font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -17px;\n	border: 1px solid #3C7FB1;\n}\n\n.btnmain {\n	color: #000000;\n	font-weight: bold;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -0px;\n	border: 1px solid #3A4955;\n	cursor: pointer;\n	font-size: 1.1em;\n}\n\n.btnlite {\n	color: #000000;\n	font-weight: normal;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -0px;\n	border: 1px solid #3A4955;\n	cursor: pointer;\n	font-size: 1.1em;\n}\n\n.pmbutton  {\n    overflow: visible;\n}\n\n.btnmain:hover {\n	color: #000000;\n	font-weight: bold;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -17px;\n	border: 1px solid #3C7FB1;\n	cursor: pointer;\n	font-size: 1.1em;\n}\n\n.btnlite:hover {\n	color: #000000;\n	font-weight: normal;\n	background-image: url("styles/AeroBlack/theme/images/bg_button.gif");\n    background-repeat: repeat-x;\n    background-position: -0px -17px;\n	border: 1px solid #3C7FB1;\n	cursor: pointer;\n	font-size: 1.1em;\n}\n\n.btnfile {\n	font-weight: normal;\n	background-color: #FFFFFF;\n	border: 1px solid #3C7FB1;\n	padding: 1px 5px;\n	font-size: 1.1em;\n}\n\n.helpline {\n	background-color: #E4F4FE;\n	border-style: none;\n}\n\n\n/* BBCode\n ------------ */\n.quotetitle {\n	margin: 10px 5px 0 5px;\n	padding: 1px 0px 1px 5px;\n	border-width: 1px 1px 0 1px;\n	border-style: solid;\n	border-color: #A7BAC5;\n	color: #000000;\n	background-image: url(''styles/AeroBlack/theme/images/rowh.gif'');\n	border-bottom: 1px solid #96D9F9;\n	font-size: 0.85em;\n	font-weight: bold;\n}\n\n.attachtitle {\n	margin: 10px 5px 0 5px;\n	padding: 4px;\n	border-width: 1px 1px 0 1px;\n	border-style: solid;\n	border-color: #A7BAC5;\n	color: #000000;\n	background-color: #EFF9FE;\n	font-size: 0.85em;\n	font-weight: bold;\n}\n\n.quotetitle .quotetitle {\n	font-size: 1em;\n}\n\n.quotecontent, .attachcontent {\n	margin: 0 5px 10px 5px;\n	padding: 1px 5px 1px 5px;\n	border-color: #A7BAC5;\n	border-width: 0 1px 1px 1px;\n	border-style: solid;\n	font-weight: normal;\n	font-size: 1em;\n	line-height: 1.4em;\n	font-family: "Lucida Grande", "Trebuchet MS", Helvetica, Arial, sans-serif;\n	background-color: #EFF9FE;\n	color: #002A46;\n}\n\n.attachcontent {\n	font-size: 0.85em;\n}\n\n.codetitle {\n	margin: 10px 5px 0 5px;\n	padding: 1px 0px 1px 5px;\n	border-width: 1px 1px 0 1px;\n	border-style: solid;\n	border-color: #A7BAC5;\n	color: #000000;\n	background-image: url(''styles/AeroBlack/theme/images/rowh.gif'');\n	border-bottom: 1px solid #96D9F9;\n	font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;\n	font-size: 0.8em;\n}\n\n.codecontent code {\n   overflow: auto;\n   display: block;\n   height: auto;\n   max-height: 200px;\n   white-space: normal;\n   margin: 0 5px 10px 5px;\n   padding: 1px 5px 1px 5px;\n   border-color: #A7BAC5;\n   border-width: 0 1px 1px 1px;\n   border-style: solid;\n   font-weight: normal;\n   color: #15A400;\n   font-size: 0.85em;\n   font-family: Monaco, ''Courier New'', monospace;\n   background-color: #EFF9FE;\n}\n\n.syntaxbg {\n	color: #FFFFFF;\n}\n\n.syntaxcomment {\n	color: #FF8000;\n}\n\n.syntaxdefault {\n	color: #0000BB;\n}\n\n.syntaxhtml {\n	color: #000000;\n}\n\n.syntaxkeyword {\n	color: #007700;\n}\n\n.syntaxstring {\n	color: #DD0000;\n}\n\n\n/* Private messages\n ------------------ */\n.pm_marked_colour {\n	background-color: #000000;\n}\n\n.pm_replied_colour {\n	background-color: #A9B8C2;\n}\n\n.pm_friend_colour {\n	background-color: #007700;\n}\n\n.pm_foe_colour {\n	background-color: #DD0000;\n}\n\n\n/* Misc\n ------------ */\nimg {\n	border: none;\n}\n\n.nb {\n	border: none;\n	background: none;\n}\n\n.sep {\n	color: black;\n	background-color: #FFA34F;\n}\n\ntable.colortable td {\n	padding: 0;\n}\n\npre {\n	font-size: 1.1em;\n	font-family: Monaco, ''Courier New'', monospace;\n}\n\n.nowrap {\n	white-space: nowrap;\n}\n\n.username-coloured {\n	font-weight: bold;\n}\n\n.buttons {\n	float: left;\n	width: auto;\n	height: auto;\n}\n\n/* Rollover state */\n.buttons div {\n	float: left;\n	margin: 0 5px 0 0;\n	background-position: 0 100%;\n}\n\n/* Rolloff state */\n.buttons div a {\n	display: block;\n	width: 100%;\n	height: 100%;\n	background-position: 0 0;\n	position: relative;\n	overflow: hidden;\n}\n\n.profile-icons {\n	float: right;\n	width: auto;\n	height: auto;\n}\n\n/* Rollover state */\n.profile-icons div {\n	float: right;\n	margin: 0 0 5px 5px;\n	background-position: 0 100%;\n}\n\n/* Rolloff state */\n.profile-icons div a {\n	display: block;\n	width: 100%;\n	height: 100%;\n	background-position: 0 0;\n	position: relative;\n	overflow: hidden;\n}\n\n.review-icons {\n	float: right;\n	width: auto;\n	height: auto;\n}\n\n/* Rollover state */\n.review-icons div {\n	float: right;\n	margin: 5px 5px 5px 0;\n	background-position: 0 100%;\n}\n\n/* Rolloff state */\n.review-icons div a {\n	display: block;\n	width: 100%;\n	height: 100%;\n	background-position: 0 0;\n	overflow: hidden;\n}\n\n/* Hide <a> text and hide off-state image when rolling over (prevents flicker in IE) */\n/*.buttons div span		{ display: none; }*/\n/*.buttons div a:hover	{ background-image: none; }*/\n.buttons div span			{ position: absolute; width: 100%; height: 100%; cursor: pointer;}\n.buttons div a:hover span	{ background-position: 0 100%; }\n\n/* Big button images */\n.reply-icon span, .post-icon span, .locked-icon span	{ background: transparent none 0 0 no-repeat; }\n.pmreply-icon span, .newpm-icon span, .forwardpm-icon span	{ background: none 0 0 no-repeat; }\n\n/* Set big button dimensions */\n.buttons div.reply-icon, .buttons div.post-icon, .buttons div.locked-icon, .buttons div.pmreply-icon, .buttons div.newpm-icon, .buttons div.forwardpm-icon		{ width: 91px; height: 30px; }\n\n/* Hide <a> text and hide off-state image when rolling over (prevents flicker in IE) */\n.profile-icons span { display:none; }\n.profile-icons a:hover { background: none; }\n.review-icons span { display:none; }\n.review-icons a:hover { background: none; }\n\n/* Profile & navigation icons */\n.quote-icon, .quote-icon a, .report-icon, .report-icon a, .warn-icon, .warn-icon a, .edit-icon, .edit-icon a, .delete-icon, .delete-icon a, .info-icon, .info-icon a	{ background: none top left no-repeat; }\n\n/* Set profile icon dimensions */\n.profile-icons div.quote-icon, .review-icons div.quote-icon	{ width: 43px; height: 18px; }\n.profile-icons div.report-icon, .profile-icons div.info-icon, .profile-icons div.warn-icon	{ width: 19px; height: 18px; }\n.profile-icons div.edit-icon, .review-icons div.edit-icon	{ width: 34px; height: 18px; }\n.profile-icons div.delete-icon, .review-icons div.delete-icon	{ width: 17px; height: 18px; }\n\n.reply-icon span	{ background-image: url("{T_IMAGESET_LANG_PATH}/button_topic_reply.png"); }\n.post-icon span		{ background-image: url("{T_IMAGESET_LANG_PATH}/button_topic_new.png"); }\n.locked-icon span	{ background-image: url("{T_IMAGESET_LANG_PATH}/button_topic_locked.png"); }\n.pmreply-icon span	{ background-image: url("{T_IMAGESET_LANG_PATH}/button_pm_reply.png"); }\n.newpm-icon span 	{ background-image: url("{T_IMAGESET_LANG_PATH}/button_pm_new.png"); }\n.forwardpm-icon span	{ background-image: url("{T_IMAGESET_LANG_PATH}/button_pm_forward.png"); }\n\n/* Profile & navigation icons */\n.quote-icon, .quote-icon a		{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_post_quote.png"); }\n\n/* Moderator icons */\n.report-icon, .report-icon a		{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_post_report.png"); }\n.edit-icon, .edit-icon a			{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_post_edit.png"); }\n.delete-icon, .delete-icon a		{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_post_delete.png"); }\n.info-icon, .info-icon a			{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_post_info.png"); }\n.warn-icon, .warn-icon a			{ background-image: url("{T_IMAGESET_LANG_PATH}/icon_user_warn.png"); }');
